import Layout from "../components/MyLayout";
import Link from "next/link";
import fetch from "isomorphic-unfetch";
import { Typography, Divider, PageHeader, Card, Icon, Avatar } from "antd";
const { Title, Paragraph, Text } = Typography;

interface IContext {
  req: IReq;
}

interface IReq {
  headers: IHeaders;
}

interface IHeaders {
  "user-agent": string;
}

interface DefaultProps {
  shows: any[];
  userAgent: String;
}

interface IItem {
  show: object;
}

interface IShow {
  id: string;
  name: string;
  summary: string;
  image: {
    medium: string;
  };
}

const Index = (props: DefaultProps) => (
  <Layout>
    <Title>Batman TV Shows</Title>
    <Paragraph>
      <Paragraph>
        蚂蚁的企业级产品是一个庞大且复杂的体系。这类产品不仅量级巨大且功能复杂，而且变动和并发频繁，常常需要设计与开发能够快速的做出响应。同时这类产品中有存在很多类似的页面以及组件，可以通过抽象得到一些稳定且高复用性的内容。
      </Paragraph>
    </Paragraph>
    <h2></h2>
    <ul>
      {props.shows.map((show: IShow) => (
        <Card style={{ width: "100%", marginTop: 16 }}>
          <Card.Meta
            avatar={<Avatar src={show.image.medium} />}
            title={show.name}
            description={show.summary}
          />
        </Card>
      ))}
    </ul>
    <style jsx>{`
      h1,
      a {
        font-family: "Arial";
      }

      ul {
        padding: 0;
      }
    `}</style>
  </Layout>
);

Index.getInitialProps = async function(context: IContext) {
  const res = await fetch("https://api.tvmaze.com/search/shows?q=batman");
  const data = await res.json();
  const userAgent = context.req
    ? context.req.headers["user-agent"]
    : navigator.userAgent;
  return {
    shows: data.map((item: IItem): object => item.show),
    userAgent
  };
};

export default Index;
